test.status.script:
if( message['event']['error_code'] != null) { 'error' }
else if ( message['event']['result'].match(/.*INVALID.*/) ) { 'invalid' }
else if ( message['event']['result'].match(/.*ERROR.*/) ) { 'error' }
else if ( message['event']['result'].match(/.*NO RESULT.*/) ) { 'no_result' }
else if ( message['event']['result'].match(/.*Rif Resistance.*/) ) { 'success' }
else if ( message['event']['result'].match(/.*MTB DETECTED.*/) ) { 'success' }
else { 'error' }

test.assays.result.script:
if(message['event']['result'].match(/.*INVALID.*/) || message['event']['result'].match(/.*ERROR.*/) || message['event']['result'].match(/.*NO RESULT.*/)) {
  ['n/a', 'n/a']
} else {
  if(message['event']['result'].match(/.*MTB DETECTED.*/)){
    mtb = 'positive'
  } else {
    mtb = 'negative'
  };
  if(message['event']['result'].match(/.*Rif Resistance DETECTED.*/)){
    rif = 'positive'
  } else {
    rif = 'negative'
  };
  [mtb, rif]
}
